hudson.tasks.Shell.command:
#!/bin/bash echo "waitting for a restart" sleep 90
for i in {0..1500..60} do echo "Waiting for the build device to become ready! $i" sleep 60 if ping $build_machine -c 1 then echo device is up and ready! sleep 30 exit 0 fi done
echo timeout waiting for the device to become ready! exit 1
